generated.css:
html,body {
  width: 100%;
  background-color:#f5f5f5;
  margin: 0;
  padding: 0;
  display:inline-block;
}
.todoapp {
  text-align: center;
}
.infor {
  text-align: center;
  color:rgba(0,0,0,0.3);
  margin: 50px;
}
.infor a {
  color:rgba(0,0,0,0.3);
  text-decoration: none;
}
.infor a:hover {
  text-decoration-line: underline;
}
.header {
  margin:0;
  padding: 0;
}
h1 {
  font-family: monospace;
  color: #b5b5b5;
  font-size: 50px;
  font-weight: 800;
  margin: 35px;
}
.new-todo {
  box-sizing: border-box;
  padding: 0px 0px 0px 60px;
  font-size: 20px;
  border: none;
  width: 500px;
  height: 40px;
  border: rgba(0,0,0,0.1) 1px solid;
  background: rgba(0,0,0,0.003);
  font-family: monospace;
}
.new-todo::-webkit-input-placeholder { /* WebKit, Blink, Edge */
  font-family: monospace;  
  font-style: italic;
  font-weight: 700;
  font-size:20px;
  line-height:initial;
  color: rgba(0,0,0,0.3)
}
.new-todo:active {
  outline: none;
}
.new-todo:hover {
  outline: none;
}
.new-todo:focus {
  outline: none;
}
.toggle-all {
  background-color: rgba(0,0,0,0.003);
  color: rgba(0,0,0,0.5);
  -webkit-appearance: none;
  position: absolute;
  left: 8px;
  top: 5px;
}
.toggle-all:focus{
  outline-color: rgba(0,0,0,0.003);
}
.toggle-all:before {
  font-size: 20px;
  font-weight: 800;
}
.filters {
  display: inline-block;
  margin: 0;
  padding:0;
}
.filters li {
  display: inline-block;
  margin: 0;
  padding:0;
}
.footer {
  box-sizing: border-box;
  width: 500px;
  margin: 0 auto;
  height: 40px;
  padding: 0;
  border: rgba(0,0,0,0.1) 1px solid;
  display:flex;
  flex-direction: row;
  justify-content: space-around;
  align-items: center;
}
.main {
  margin: 0 auto;
  padding: 0;
}
.todo-list {
  width: 500px;
  margin: 0 auto;
  text-align: center;
  list-style: none;
  padding: 0;
}
.todo {
  box-sizing: border-box;
  width: 500px;
  margin:0 auto;
  height: auto;
  border: rgba(0,0,0,0.1) 1px solid;
}
.new-todo-wrapper {
  /* margin: 0 auto; */
  display: inline-block;
  width:500px;
  position: relative;
}
.button-wrapper {
  display: inline-block;
  width: 113px;
}
.filters li a {
  text-decoration: none;
  color: rgba(0,0,0,0.3);
  padding: 1px;
}
.filters li a.selected {
  color: black;
  border-radius: 5px;
}
.clear-completed{
  -webkit-appearance:none;
  appearance:none;
  border:none;
  background:#f5f5f5;
}
.todo-count {
  width: 86px;
}
.todo .edit {
  display: none;
  width: 400px;
  font-size: 20px;
  margin-left: 40px;
  padding-left: 6px;
  padding: 3px 4px 3px 13px;
  font-family: monospace;
}
.todo .edit:focus {
  outline-color: gray;
}
.todo {
  display: flex;
  justify-content: flex-start;
  align-items: center;
}
.todo-list .editing .edit {
  display: inline-block
} 
.todo-list .editing .view {
  display:none;
}
.el-checkbox {
  width:50px;
}
.el-checkbox__input.is-checked .el-checkbox__inner, .el-checkbox__input.is-indeterminate .el-checkbox__inner {
  background-color: rgb(148, 148, 148) !important;
  border-color: rgb(148, 148, 148) !important;
}
.el-checkbox__inner:hover {
  border-color: rgb(119, 119, 119) !important;
}
.el-checkbox .el-checkbox__input.is-focus .el-checkbox__inner {
  border-color: #b5b5b5 !important;
}
.todo-title {
  width: 400px;
  height: auto;
  text-align: start;
  word-wrap: break-word;
  font-size:20px;
  display: inline-block;
  padding: 5px;
  font-family: monospace;
}
.el-button--text {
  color: gray !important;
  display: none !important;
  padding: 0 !important;
  margin:0 auto !important;
}
.view {
  display: flex;
  align-items: center;
}
.todo:hover .el-button--text {
  display: inline-block !important;
}
.el-icon-close {
  font-size: 15px;
  font-weight: 800 !important;
}
.todo-list .todo.completed .todo-title {
  text-decoration-line: line-through;
  text-decoration-color: gray;
  color: gray;
}
[v-cloak] {
  display: none
} 
